import base64
from PIL import Image, ImageFile
from io import BytesIO

# Bật chế độ LOAD_TRUNCATED_IMAGES để xử lý lỗi file bị cắt
ImageFile.LOAD_TRUNCATED_IMAGES = True

# Chuỗi Base64 từ vietQRImage
vietQRImage = ""  # Rút gọn

# Giải mã Base64 và xử lý ảnh
try:
    image_data = base64.b64decode(vietQRImage)
    image = Image.open(BytesIO(image_data))

    # Lưu và hiển thị ảnh
    image.save("vietQR.png")
    image.show()
except Exception as e:
    print("Lỗi khi xử lý ảnh:", e)
